/* LaserGateLoop3.bsh
* This program will take a series of images after exposing a laser gate for a set duration
* There is a pause or a user set amount of time in beween two loops.
* The first image is before the gate pulse
* The shutter trigger is Arduino pin 13 which should be connected to the Pulse Input of
* the Uniblitz shutter. The shutter should be set to N.O. and Remote
* Heather Bomberger Summer 2014 University of Georgia
*/
gui.clearMessageWindow();
//USER INPUT
float ImageDuration = 800; //Time beween flashes (ms)
GatePulse = 100; //Time laser is on (ms)
int LoopOne = 35; //Number of flashes before the pause int LoopTwo = 35; //Number of flashes after the pause
Pause = 10; //Duration of pause (ms) (The ImageDuration will be added to this pause)
EMgain = 151; //Gain
Exposure = 100; //Exposure (ms)
//User Inerface (Delete "/*" and "*/" to use)
/* import ij.gui.GenericDialog;
GenericDialog gd = new GenericDialog("LaserGateLoop");
gd.addNumericField("Exposure", Exposure, 0);
gd.addNumericField("EM Gain", EMgain, 0);
gd.addNumericField("Gate Pulse", GatePulse, 0);
gd.addNumericField("Image Duration", ImageDuration, 0);
gd.addNumericField("Loop One", LoopOne, 0);
gd.addNumericField("Loop Two", LoopTwo, 0);
gd.addNumericField("Pause", Pause, 0);
gd.showDialog();
Exp = (int) gd.getNextNumber();
Gain = (int) gd.getNextNumber();
GatePulse = (int) gd.getNextNumber();
ImageDuration = (int) gd.getNextNumber();
LoopOne = (int) gd.getNextNumber();
LoopTwo = (int) gd.getNextNumber();
Pause = (int) gd.getNextNumber();
print (Exp); print (Gain);
print (GatePulse); print (ImageDuration);
print (LoopOne); print (LoopTwo);
print (Pause);
*/
//Check Statements
if (Exposure >1000|| Exposure <10) {
   gui.message("Exposure not in range.");
   break;
}
if (EMgain >300|| EMgain <0) {
   gui.message("EMgain not in range.");
   break;
} if (ImageDuration >5000 || ImageDuration <0) {
   gui.message("Image Duration not in range.");
   break;
} if (GatePulse > 500 || GatePulse < 0) {
   gui.message("Gate Pulse not in range.");
   break;
} if (LoopOne >100 || LoopOne <0) {
   gui.message("Loop One not in range.");
   break;
} if (LoopTwo > 100 || LoopTwo < 0) {
   gui.message("Loop Two not in range.");
   break;
} if (Pause >5000 || Pause <0) {
   gui.message("Pause not in range.");
   break;
}
Camera = mmc.getCameraDevice();
mmc.setProperty(Camera, "Exposure", Exposure);
mmc.setProperty(Camera, "Gain", EMgain);
Interval = mmc.getProperty(Camera, "ActualInterval-ms");
FloatInterval = Float.valueOf(Interval);
int PauseFrames = (int) (Pause/FloatInterval);
print("Interval: " +FloatInterval); print(ImageDuration / FloatInterval);
mmc.setProperty("Arduino-Switch", "State", 32);
mmc.setProperty("Arduino-Shutter", "OnOff", 0);
mmc.setAutoShutter(false);
//auto shutter off gui.enableLiveMode(false);
//live mode off int nrFrames = (int) (ImageDuration / FloatInterval);
int frame = 0;
int nrImages = nrFrames*(LoopOne+LoopTwo)+1 +  PauseFrames;
int imgcnt = 0; acqName = "Test";
rootDirName = "C:/Users/Lechtreck Lab/Documents/Heather-MM";
nrChannels = 1;
nrSlices = 1;
int channel = 0;
int slice = 0;
width = mmc.getImageWidth();
height = mmc.getImageHeight();
bytes = mmc.getBytesPerPixel();
depth = mmc.getImageBitDepth();
print ("width: " +width);
print ("height: " +height);
print ("depth: " +depth);
print ("bytes: " +bytes);
print ("Frames: " +nrFrames);
gui.closeAllAcquisitions();
gui.openAcquisition(acqName, rootDirName, nrImages, nrChannels, nrSlices); gui.initializeAcquisition(acqName, (int) width, (int) height, (int) bytes, (int) depth);
//gui.setAcquisitionProperty(acqName, "Pixel width", "2.0000");
//mmc.setProperty("Properties", "Pixel width", 2);
now = System.currentTimeMillis();
// Acquire a pre-bleaching image and add it to the acquisition at the beginning
mmc.snapImage();
PreImage = mmc.getImage();
gui.addImage(acqName, PreImage, frame, channel, slice);
imgcnt++; //the acquisition will start on frame 2
gui.setContrastBasedOnFrame(acqName, frame, slice −1);
//laser gate for loop One
for (n = 0; n<LoopOne; n++){
  frame = 0;
  mmc.setProperty("Arduino-Shutter", "OnOff", 1);
  mmc.sleep(GatePulse);
  mmc.setProperty("Arduino-Shutter", "OnOff", 0);
  mmc.startSequenceAcquisition(nrFrames, 0, false);
  while (mmc.getRemainingImageCount()>0 || mmc.isSequenceRunning(mmc.getCameraDevice()))
{
   if (mmc.getRemainingImageCount()>0) {
    img = mmc.popNextImage();
   if (frame<nrFrames) {//take designated amount of images
   gui.addImage(acqName, img, imgcnt, channel, slice);
    frame++;
    imgcnt++;
  }
   }
 }
}
//Pause while camera recording
  frame = 0;
  mmc.startSequenceAcquisition(PauseFrames, 0, false);
  while (mmc.getRemainingImageCount()>0 || mmc.isSequenceRunning(mmc.getCameraDevice()))
{
   if (mmc.getRemainingImageCount()>0) {
    img = mmc.popNextImage();
   if (frame<PauseFrames) {//take designated amount of images
   gui.addImage(acqName, img, imgcnt, channel, slice);
    frame++;
    imgcnt++;
  }
   }
 }
//Loop Two
for (n = 0; n<LoopTwo; n++){
  frame = 0;
  mmc.setProperty("Arduino-Shutter", "OnOff", 1);
  mmc.sleep(GatePulse);
  mmc.setProperty("Arduino-Shutter", "OnOff", 0);
  mmc.startSequenceAcquisition(nrFrames, 0, false);
  while (mmc.getRemainingImageCount()>0 || mmc.isSequenceRunning(mmc.getCameraDevice()))
{
   if (mmc.getRemainingImageCount()>0) {
    img = mmc.popNextImage();
   if (frame<nrFrames) {//take designated amount of images
   gui.addImage(acqName, img, imgcnt, channel, slice);
    frame++;
    imgcnt++;
  }
   }
 }
}
duration = (System.currentTimeMillis() - now);
gui.setContrastBasedOnFrame(acqName, frame, slice −1);
mmc.stopSequenceAcquisition();
gui.promptToSaveAcquisition(acqName,false);
gui.closeAcquisition(acqName);
print ("Duration = " +duration +" ms");
mmc.setProperty("Arduino-Shutter", "OnOff", 0);
