p.code:
cd ${READS} 
for i in `ls | grep trimmed.fq`; do 
echo ${i}

for i in `ls | grep accepted_hits.bam`; do 
echo ${i} 
module load anaconda

htseq-count -f bam -s reverse -m union --idattr gene_id -o ${OUF}/${i}_Rev.htseq ${i} 
${GENOME} > ${OUF}/${i}_Rev.count
 done

for i in `ls | grep accepted_hits.bam`; do 
echo ${i} 
module load anaconda

htseq-count -f bam -s no -m union --idattr gene_id -o ${OUF}/${i}_Rev.htseq ${i}
 ${GENOME} > ${OUF}/${i}_Rev.count 
done
