IMECM = im2bw(IMECM); 
nwhite = nnz(IMECM); 
[rows,cols]=find(IMECM == 1); 
Time(k)=(k-1)*15; 
area(k)=nwhite*0.2*0.2; 
width(k)=std(cols); %./mean(cols); 
height(k)=std(rows); %./mean(rows); 
