Batch Macro processing code for aggregation estimates in ImageJrun("Montage to Stack...", "images_per_row = 16 images_per_column = 16 border = 0"); 
 macro "Measure Stack" {
saveSettings;
setOption("Stack position", true);
for (n = 1; n <= nSlices; n++) {
setSlice(n);
setAutoThreshold("Default dark stack");
//run("Threshold...");
run("Create Selection");;
run("Measure");
}
restoreSettings;
}
